import ReactDom from 'react-dom'
import React, {Component} from 'react'
import {InputText, Button} from '../../../src/index'

class RegisterForm extends Component 
{
  constructor(props)
  {
    super(props)
    this.state = {}
  }

  render()
  {
    console.log(this.state)
    return (
      <div className="form-container">
        <strong>Generated by react-form-master</strong><br/>
        <small>available at <a style={{color:'#FFF'}} href="https://www.npmjs.com/package/react-form-master" target="_blank">react-form-master</a></small>
        <hr/>
        <br/>
        <InputText 
          type='email'
          name='email'
          label='Your Email'
          required={true}
          value={this.state.email || ''}
          validation={this.state.email_validation || null}
          placeholder='Input your email'
          info='Max 30 characters'
          count={true}
          max={30}
          setState={(obj) => this.setState(obj)}
        />
        <InputText 
          type='text'
          name='username'
          label='Your Username'
          required={true}
          value={this.state.username || ''}
          validation={this.state.username_validation || null}
          placeholder='Input your username'
          setState={(obj) => this.setState(obj)}
        />
        <InputText 
          type='password'
          name='password'
          label='Your Password'
          required={true}
          value={this.state.password || ''}
          validation={this.state.password_validation || null}
          placeholder='Input your pasword'
          setState={(obj) => this.setState(obj)}
        />
        <InputText 
          type='url'
          name='web'
          label='Your Website/Blog'
          required={true}
          value={this.state.web || ''}
          validation={this.state.web_validation || null}
          placeholder='Input your web/blog url'
          setState={(obj) => this.setState(obj)}
        />
        <Button
        
         />
      </div>
    )
  }
}

ReactDom.render(
  <RegisterForm />,
  document.getElementById('app')
)